import React, { useState } from "react";
import { Button, Grid, colors } from '@mui/material';
import mobiscroll from '@mobiscroll/react';
import '@mobiscroll/react/dist/css/mobiscroll.min.css';


const ScientificKeyboard = ({ handleInput }) => {
  const [keys, setKeys] = useState([  "sin", "cos", "tan", "cot", "sec", "csc", "asin", "acos", "atan", "acot", "asec", "acsc",  "log", "ln", "exp", "√", "xⁿ", "x²", "x³", "∫", "∬", "∭", "∮", "∯", "∰", "∇",  "(", ")", "[", "]", "{", "}", "π", "e", "φ", "γ", "ϕ", "θ", "λ", "µ", "ν", "ρ", "σ", "τ", "ω",  "<", ">", "=", "≠", "≈", "≅", "≡", "≢", "≺", "≻", "≼", "≽", "∈", "∉", "∋", "∌",  "∪", "∩", "⊆", "⊇", "⊂", "⊃", "∅", "∀", "∃", "∄", "∆", "∂", "∞", "∑", "∏", "∐",  "!", "%", "±", "/", "*", "-", "+", ".", "^",  "⋯", "⋮", "∴", "∵", "↔", "→", "←", "↑", "↓", "⊕", "⊗", "⊖", "⊘", "∺", "⊙", "⊚", "⊛", "⊜",  "⊝", "⊞", "⊟", "⊠", "⊡", "∛", "∜", "∝", "∟", "∠", "∡", "∢", "∣", "∥", "∦", "∧", "∨",  "∻", "∽", "∾", "∿", "≀", "≁", "≂", "≃", "≄", "≆", "≇", "≉", "≊", "≋", "≌", "≍",  "≎", "≏", "≐", "≑", "≒", "≓", "≔", "≕", "≖", "≗", "≘", "≙", "≚", "≛", "≜", "≝", "≞", "≟",  "≣", "≤", "≥", "≦", "≧", "≨", "≩", "≪", "≫", "≬", "≭", "≮", "≯", "≰", "≱",  "≲", "≳"]);

   


  const handleClick = (value) => {

    // setKeys(value) 
    handleInput(value);
    console.log("clicked", value);
  };

  const[numb, setNumb] = useState(" ")
  const handleChange = (event) => {
    setNumb(event.target.value);
    console.log("changed the value when pressed the numbers ");
  }

class App extends React.Component {
    render() {
        return (
            <div>
                <mobiscroll.Numpad
                    ref="numpad"
                    theme="ios" 
                    themeVariant="light"
                    preset="decimal"
                    scale={0}
                    max={9999}
                    min={-9999}
                >
                    <input placeholder="Please Select..."/>
                </mobiscroll.Numpad>
            </div>
        );
    }    
}


  return (
    <div className="scientific-keyboard">
      <Grid container spacing={0.6}>
        {keys.map((key) => (
          <Grid item xs={0.6} key={key}>
            <Button style={{ width: '50px', height: '50px', color: "black", padding: "20px", border: "5px solid grey", backgroundColor: 'white' }} variant="contained" onClick={() => handleClick(key)}>
              {key}
            </Button>
          </Grid>
        ))}
      </Grid>
      
    </div>
  );

};

export default ScientificKeyboard;
