import React, { useState } from "react";
import ScientificKeyboard from "./components/scientificKeyboard ";
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import ListItemText from '@mui/material/ListItemText';
import ListItem from '@mui/material/ListItem';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import CloseIcon from '@mui/icons-material/Close';
import Slide from '@mui/material/Slide';
import { WindowSharp } from "@mui/icons-material";


const App = () => {
  const [input, setInput] = useState("");

  const handleInput = (value) => {
    setInput(input + value);
  };
  const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    window.location.reload();

    setOpen(false);
  }



  return (
    <div className="app">
      <h1>The list of basic integral formulas are

        ∫ 1 dx = x + C
        ∫ a dx = ax+ C
        ∫ xn dx = ((xn+1)/(n+1))+C ; n≠1
        ∫ sin x dx = – cos x + C
        ∫ cos x dx = sin x + C
        ∫ sec2x dx = tan x + C
        ∫ csc2x dx = -cot x + C
        ∫ sec x (tan x) dx = sec x + C
        ∫ csc x ( cot x) dx = – csc x + C
        ∫ (1/x) dx = ln |x| + C
        ∫ ex dx = ex+ C
        ∫ ax dx = (ax/ln a) + C ; a > 0,  a≠1</h1>



      <p>
        <Button variant="outlined" onClick={handleClickOpen} style={{ backgroundColor: "Grey", color: "white" }}>
          Click to Open Math Keyboard
        </Button>
        <Dialog
          fullScreen
          open={open}
          onClose={handleClose}
          TransitionComponent={Transition}
        >
          <AppBar sx={{ position: 'relative' }}>
            <Toolbar>
              <IconButton
                style={{ backgroundColor: "white", color: "grey" }}
                edge="start"
                color="inherit"
                onClick={handleClose}
                aria-label="close"
              >
                <CloseIcon />
              </IconButton>
              <Typography sx={{ ml: 2, flex: 1 }} style={{ backgroundColor: "white", color: "grey" }} variant="h6" component="div">
                Scientific Keyboard
              </Typography>
              <Button autoFocus color="inherit" style={{ backgroundColor: "grey", border: "1px black", width: "60px" }} onClick={handleClose}>
                save
              </Button>
            </Toolbar>
          </AppBar>
          <List>
            <input type="text" value={input} size="150" style={{ width: '1400px', height: '50px' }} />

            <ScientificKeyboard handleInput={handleInput} />
            <Button autoFocus color="inherit" style={{
              float: "right"
              , backgroundColor: "grey", border: "1px black", width: "60px"
            }} onClick={handleClose}>
              Close
            </Button>

          </List>
        </Dialog>
      </p>
      <p>
      </p>
      <p>
      </p>


    </div>
  );
};

export default App;
