import React, { useState } from "react";
import { Button, Grid, colors } from '@mui/material';
import "../App.css"





const ScientificKeyboard = ({ handleInput }) => {
  const [keys, setKeys] = useState(["\\sin", "\\cos", "\\tan", "\\cot", "\\sec", "\\csc", "\\arcsin", "\\arccos", "\\arctan", "\\text{acot}", "\\text{asec}", "\\text{acsc}", "\\log", "\\ln", "\\exp", "\\sqrt{}", "x^n", "x^2", "x^3", "\\int", "\\iint", "\\iiint", "\\oint", "\\oiint", "\\oiiint", "\\nabla", "(", ")", "[", "]", "{{}", "}", "\\pi", "\\text{e}", "\\varphi", "\\gamma", "\\phi", "\\theta", "\\lambda", "\\mu", "\\nu", "\\rho", "\\sigma", "\\tau", "\\omega", "<", ">", "=", "\\neq", "\\approx", "\\cong", "\\equiv", "\\not\\equiv", "\\prec", "\\succ", "\\preceq", "\\succeq", "\\in", "\\notin", "\\ni", "\\not\\ni", "\\cup", "\\cap", "\\subseteq", "\\supseteq", "\\subset", "\\supset", "\\varnothing", "\\forall", "\\exists", "\\nexists", "\\Delta", "\\partial", "\\infty", "\\sum", "\\prod", "\\coprod", "!", "%", "\\pm", "/", "*", "-", "+", ".", "\\wedge", "\\ldots", "\\vdots", "\\therefore", "\\leftrightarrow", "\\rightarrow", "\\leftarrow", "\\uparrow", "\\downarrow", "\\oplus", "\\otimes", "\\ominus", "\\oslash", "\\dotplus", "\\odot", "\\circledcirc", "\\circledast", "\\circledot", "\\boxplus", "\\boxminus", "\\boxtimes", "\\boxdot", "\\sqrt[3]{}", "\\sqrt[4]{}", "\\propto", "^\\circ", "\\rightangle", "\\angle", "\\measuredangle", "\\sphericalangle", "\\mid", "\\parallel", "\\nparallel", "\\land", "\\lor", "\\hbar", "\\sim", "\\backsim", "\\wr", "\\asymp", "\\nsim", "\\eqsim", "\\lesssim", "\\gtrsim", "\\lessapprox", "\\gtrapprox", "\\precapprox", "\\succapprox", "\\subsetneq", "\\supsetneq", "\\leq", "\\geq", "\\leqq", "\\geqq", "\\nleq", "\\ngeq", "\\ll", "\\gg", "\\between", "\\nasymp", "\\pitchfork", "\\because"]);



  const [input, setInput] = useState('');

  const handleClick = (value) => {

    handleInput(value);
    console.log("clicked", value);



  };



  const [numb, setNumb] = useState(" ")
  const handleChange = (event) => {
    setNumb(event.target.value);
    console.log("changed the value when pressed the numbers ");
  }


  return (
    <div className="scientific-keyboard">
      <Grid container spacing={0.6}>
        {keys.map((symbol, index) => (
          <Grid item xs={0.6} key={symbol}>
            <Button className="button" key={symbol} id="my-btn" style={{
              width: '50px',
              height: '50px',
              color: 'black',
              padding: '20px',
              border: '5px solid grey',
              backgroundColor: 'white',
              borderRadius: '10px', // Add a border radius of 10px
              boxShadow: '2px 2px 5px rgba(0, 0, 0, r0)' // Add a box shadow
            }} variant="contained" onClick={() => handleClick(symbol)}>
              {symbol}
            </Button>

          </Grid>
        ))}
      </Grid>

    </div>
  );

};




export default ScientificKeyboard;
