import React, { useState } from "react";
import { Button, Grid, colors } from '@mui/material';


const ScientificKeyboard = ({ handleInput }) => {
  const [keys, setKeys] = useState(["sin", "cos", "tan", "cot", "sec", "csc", "asin", "acos", "atan", "acot", "asec", "acsc", "log", "ln", "exp", "√", "xⁿ", "x²", "x³", "∫", "∬", "∭", "∮", "∯", "∰", "∇", "(", ")", "[", "]", "{", "}", "π", "e", "φ", "γ", "ϕ", "θ", "λ", "µ", "ν", "ρ", "σ", "τ", "ω", "<", ">", "=", "≠", "≈", "≅", "≡", "≢", "≺", "≻", "≼", "≽", "∈", "∉", "∋", "∌", "∪", "∩", "⊆", "⊇", "⊂", "⊃", "∅", "∀", "∃", "∄", "∆", "∂", "∞", "∑", "∏", "∐", "!", "%", "±", "/", "*", "-", "+", ".", "^", "⋯", "⋮", "∴", "∵", "↔", "→", "←", "↑", "↓", "⊕", "⊗", "⊖", "⊘", "∺", "⊙", "⊚", "⊛", "⊜", "⊝", "⊞", "⊟", "⊠", "⊡", "∛", "∜", "∝", "°", "∟", "∠", "∡", "∢", "∣", "∥", "∦", "∧", "∨", "∻", "∽", "∾", "∿", "≀", "≁", "≂", "≃", "≄", "≆", "≇", "≉", "≊", "≋", "≌", "≍", "≎", "≏", "≐", "≑", "≒", "≓", "≔", "≕", "≖", "≗", "≘", "≙", "≚", "≛", "≜", "≝", "≞", "≟", "≣", "≤", "≥", "≦", "≧", "≨", "≩", "≪", "≫", "≬", "≭", "≮", "≯", "≰", "≱", "≲", "≳"]);




  const handleClick = (value) => {

    // setKeys(value) 
    handleInput(value);
    console.log("clicked", value);
    mjAPI.config({
      extensions: "TeX-MML-AM_HTMLorMML.js",
      jax: ["input/TeX", "output/SVG"],
      displayMath: [["$$", "$$"]],
      TeX: { extensions: ["AMSmath.js", "AMSsymbols.js"] },
    });

    // Start MathJax-node
    mjAPI.start();

    // Example input string
    const input = "sin(45°)+sin(50°)";

    // Convert input to LaTeX and output SVG image
    mjAPI.typeset({
      math: input,
      format: "TeX",
      svg: true,
    }, function (data) {
      if (!data.errors) {
        const output = data.svg;
        console.log(output);
      } else {
        console.log(data.errors);
      }
    });
  };

  const [numb, setNumb] = useState(" ")
  const handleChange = (event) => {
    setNumb(event.target.value);
    console.log("changed the value when pressed the numbers ");
  }


  return (
    <div className="scientific-keyboard">
      <Grid container spacing={0.6}>
        {keys.map((key) => (
          <Grid item xs={0.6} key={key}>
            <Button id="my-btn" style={{ width: '50px', height: '50px', color: "black", padding: "20px", border: "5px solid grey", backgroundColor: 'white' }} variant="contained" onClick={() => handleClick(key)}>
              {key}
            </Button>
          </Grid>
        ))}
      </Grid>

    </div>
  );

};




export default ScientificKeyboard;
