import React, { useState } from "react";
import ScientificKeyboard from "./components/scientificKeyboard ";
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import ListItemText from '@mui/material/ListItemText';
import ListItem from '@mui/material/ListItem';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import CloseIcon from '@mui/icons-material/Close';
import Slide from '@mui/material/Slide';
import { WindowSharp } from "@mui/icons-material";
import { createTheme, ThemeProvider } from '@mui/material/styles';
import axios from "axios";
import katex from 'katex';
import "./App.css";




const App = () => {


  const fetchData = async (value) => {
    try {

      setInput("");
      console.log(input, "this is the input of the ");
      let reqUrl = 'http://localhost:5000/keyboard'
      const keyValue  = (input);

      console.log(keyValue,"this is the key value");

      await axios.post(reqUrl, { key: keyValue})

    } catch (error) {
      console.error(error);
      setInput(false);
    }
  };

  const handleChange = (event) => {
    setInput(event.target.value);
    console.log("key pressed by the user ", input);

  };

  // const element =  document.createElementById('myElement');

  const [input, setInput] = useState("");
  const string = (input)
  // const latex = katex.render(string, element,{
    //       font: 'mathit' // or another font name
    //     });

    const handleInput = (value) => {
      setInput(input+ value);
    console.log(value, "idu daan value da dai........")
    console.log(typeof(input), ":", input)
  };
  const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });

  const handleLatex={};
  const latex = katex.renderToString(`${input}${""}`); 
  // console.log(latex, "latex of  the user input///////");

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    window.location.reload();

    setOpen(false);
  }
  const handleClick = () => {
    console.log('input field submit key pressed');
  }

  // Dark theme settings.......
  const theme = createTheme({
    palette: {
      mode: 'dark',
    }
  });
  return (

  <ThemeProvider theme={theme}>

    <div className="app">
      <h1 style={{ backgroundColor: "grey", color: "white" }}>Some  of the  basic integral formulas</h1>
      <p>

        <li> ∫ 1 dx = x + C</li>
        <li>∫ a dx = ax+ C</li>
        <li>∫ xn dx = ((xn+1)/(n+1))+C ; n≠1</li>
        <li>∫ sin x dx = – cos x + C</li>
        <li>∫ cos x dx = sin x + C</li>
        <li>∫ sec2x dx = tan x + C</li>
        <li>∫ csc2x dx = -cot x + C</li>
        <li>∫ sec x (tan x) dx = sec x + C</li>
        <li>∫ csc x ( cot x) dx = – csc x + C</li>
        <li>∫ (1/x) dx = ln |x| + C</li>
        <li>∫ ex dx = ex+ C</li>
        <li>∫ ax dx = (ax/ln a) + C ; a > 0,  a≠1</li>

      </p>




      <p>
        <Button variant="outlined" onClick={handleClickOpen} style={{ backgroundColor: "Grey",  float: "center", color: 'black',
              padding: '20px',
              border: '5px solid white',
              backgroundColor: 'grey',
              borderRadius: '10px', // Add a border radius of 10px
              boxShadow: '-2px 5px 10px rgba(20, 50, 250, 50)'  }}>

        ==> Click to Open Math Keyboard
        </Button>
        <Dialog
          fullScreen
          open={open}

        // TransitionComponent={Transition}
        >
          <AppBar sx={{ position: 'relative' }} style={{ backgroundColor: "grey" }}>
            <Toolbar>
              <IconButton
                style={{ backgroundColor: "white", color: "grey", float: "right" }}
                edge="start"
                color="inherit"
                onClick={handleClose}
                aria-label="close"
              >
                <CloseIcon />
              </IconButton>
              <Typography sx={{ ml: 2, flex: 1 }} style={{ backgroundColor: "grey", color: "white" }} variant="h6" component="div">
                Scientific Keyboard
              </Typography>
              {/* <Button autoFocus color="inherit" style={{ backgroundColor: "grey", border: "1px solid black", width: "60px" }} onClick={handleClose}>
                save
              </Button> */}
            </Toolbar>
          </AppBar>
          <List>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <input type="text" value={input} onChange={handleChange} size="100" style={{ width: '1000px', height: '40px', border: "1px solid black", marginRight: '10px' }} />
              <button  onClick={fetchData} size ="150" style={{ height: '43px', backgroundColor: 'grey', width: '160px',  color: 'white', border: '1px solid black', padding: '10px ' }}>Submit</button>
            </div>
            <p></p>

            <ScientificKeyboard handleInput={handleInput} />
            <h1 style={{ backgroundColor: "grey" }}>Reciprocal Identities</h1>
              <li dangerouslySetInnerHTML={{ __html: latex }}></li>
            <p><li>cosec θ = 1/sin θ</li>
              <li>sec θ = 1/cos θ</li>
              <li>cot θ = 1/tan θ</li>
              <li>sin θ = 1/cosec θ</li>
              <li>cos θ = 1/sec θ</li>
              <li>tan θ = 1/cot θ</li></p>
            <Button autoFocus color="inherit" style={{
              float: " right"
              , backgroundColor: "grey", border: "1px black", width: "10px"
            }} onClick={handleClose}>
              Close
            </Button>


          </List>
        </Dialog>
      </p>




    </div>
  </ThemeProvider>
  );
};

export default App;
